package com.example.sarvarkhalmatov_exam_module_5.fragments

import android.os.Bundle
import android.util.Log
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.sarvarkhalmatov_exam_module_5.R
import com.example.sarvarkhalmatov_exam_module_5.adapter.CategoriesAdapter
import com.example.sarvarkhalmatov_exam_module_5.adapter.RestaurantAdapter
import com.example.sarvarkhalmatov_exam_module_5.model.Categories
import com.example.sarvarkhalmatov_exam_module_5.model.Restaurants


class HomeFragment :Fragment() {
    private  lateinit var recycler_category: RecyclerView
    private  lateinit var recycler_restaurants: RecyclerView

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        val view = inflater.inflate(R.layout.fragment_home, container, false)
        return view
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initViews(view)
    }

    private fun initViews(view: View) {
        recycler_category = view.findViewById(R.id.recycler_categories)
        recycler_category.layoutManager = LinearLayoutManager(requireContext(), LinearLayoutManager.HORIZONTAL,false)
        Log.d("@@@","keldi")
        refreshAdapterCategory(getAllCategories())

        recycler_restaurants = view.findViewById(R.id.recycler_restaurants)
        recycler_restaurants.layoutManager = GridLayoutManager(context,2)

        refreshAdapterRestaurant(getAllRestaurants())
    }



    private fun refreshAdapterCategory(allCategories: ArrayList<Categories>) {
        val adapter = CategoriesAdapter(requireContext(),allCategories)
        recycler_category.adapter = adapter

    }

    private fun refreshAdapterRestaurant(allRestaurants: ArrayList<Restaurants>) {
        val adapter = RestaurantAdapter(requireContext(),allRestaurants)
        recycler_restaurants.adapter = adapter
    }

    private fun getAllCategories(): ArrayList<Categories> {
        val categories: ArrayList<Categories> = ArrayList()

        categories.add(Categories("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQOURKtiRTos8mo5N6BG3GYMP7_b-LsmwZ3gQ&usqp=CAU","Shopping"))
        categories.add(Categories("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQobr9kGAAPQllwu5WDRbL-0PklLlCsIYGYAw&usqp=CAU","Bakery"))
        categories.add(Categories("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSvAMzu3oFzlFHqQCJnVXWGBjhemOBJQuLXRw&usqp=CAU","Stores"))
        categories.add(Categories("data:image/jpeg;base64,","Hotels"))
        categories.add(Categories("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU","Guides"))
        return categories

    }
    private fun getAllRestaurants(): ArrayList<Restaurants> {
        var restaurants:ArrayList<Restaurants> = ArrayList()
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","1","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","2","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","3","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","4","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","4","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","4","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","4","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","4","2",5f))
        restaurants.add(Restaurants("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLP5UA72mA1X4UbKoWdNy5crF2H2DV4h6YDA&usqp=CAU\"","4","2",5f))
        return restaurants

    }
}